import React, { useState } from 'react';
import './Contact.css';

export default function Contact() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const validateForm = () => {
    const newErrors = {};
    if (!name.trim()) {
      newErrors.name = 'Name is required';
    }
    if (!email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      newErrors.email = 'Email address is invalid';
    }
    if (!message.trim()) {
      newErrors.message = 'Message is required';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (validateForm()) {
      try {
        const response = await fetch('http://127.0.0.1:5000/submit', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        // Improved error handling
        if (!response.ok) {
          const errorData = await response.json();
          setErrorMessage(errorData.error || 'An error occurred while submitting the form.');
          return; // Exit early if there's an error
        }

        const result = await response.json();
        setSuccessMessage(result.message);
        setErrorMessage(''); // Clear any previous error message
      } catch (error) {
        console.error('Error submitting the form:', error);
        setErrorMessage('An error occurred while submitting the form.');
      }
    }
  };


  return (
    <div className='con'>
      <div className='contact'>
        <h1>Contact:</h1>
        <p className='ConInfo'>
          <b>Email:</b>&nbsp;&nbsp;dharshini3102k3@gmail.com<br />
          <b>Phone:</b>&nbsp;&nbsp;6281168212<br />
          <b>Location:</b>&nbsp;&nbsp;Visakhapatnam, Andhra Pradesh <br />
          <b>Pincode:</b>&nbsp;&nbsp;530011
        </p>
      </div>
      <div className='form'>
        <h1>Contact Me by a Form</h1>
        <p className='FormInfo'>
          <form onSubmit={handleSubmit}>
            <input
              type='text'
              placeholder='Name'
              id='na'
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            {errors.name && <div className="error">{errors.name}</div>}
            <br /><br />
            <input
              type='email'
              placeholder='Email'
              id='em'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {errors.email && <div className="error">{errors.email}</div>}
            <br /><br />
            <textarea
              placeholder="Message"
              id='te'
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            ></textarea>
            {errors.message && <div className="error">{errors.message}</div>}
            <br /><br />
            <input
              type='submit'
              value='Send'
              id='bu'
            />
          </form>
          {successMessage && <div className="success">{successMessage}</div>}
          {errorMessage && <div className="error">{errorMessage}</div>}
        </p>
      </div>
    </div>
  );
}
