import React, { useState } from 'react';
import './Contact.css';

export default function Contact() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState({});
  const [result, setResult] = useState('');

  const validateForm = () => {
    const newErrors = {};
    if (!name.trim()) {
      newErrors.name = 'Name is required';
    }
    if (!email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      newErrors.email = 'Email address is invalid';
    }
    if (!message.trim()) {
      newErrors.message = 'Message is required';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      const form = $(e.target);
      $.ajax({
        type: 'POST',
        url: form.attr('action'),
        data: form.serialize(),
        success: (data) => {
          setResult(data); // Update result state with server response
          // Optionally, clear form fields after successful submission
          setName('');
          setEmail('');
          setMessage('');
        },
        error: (error) => {
          console.error('Error submitting form:', error);
          // Handle error if needed
        },
      });
    }
  };

  return (
    <div className='con'>
      <div className='contact'>
        <h1>Contact:</h1>
        <p className='ConInfo'>
          <b>Email:</b>&nbsp;&nbsp;dharshini3102k3@gmail.com<br />
          <b>Phone:</b>&nbsp;&nbsp;6281168212<br />
          <b>Location:</b>&nbsp;&nbsp;Visakhapatnam, Andhra Pradesh <br />
          <b>Pincode:</b>&nbsp;&nbsp;530011
        </p>
      </div>
      <div className='form'>
        <h1>Contact Me by a Form</h1>
        <p className='FormInfo'>
          <form action='https://localhost:8000/server.php' method='post' onSubmit={handleSubmit}>
            <input
              type='text'
              placeholder='Name'
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            {errors.name && <div className='error'>{errors.name}</div>}
            <br /><br />
            <input
              type='email'
              placeholder='Email'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {errors.email && <div className='error'>{errors.email}</div>}
            <br /><br />
            <textarea
              placeholder='Message'
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            ></textarea>
            {errors.message && <div className='error'>{errors.message}</div>}
            <br /><br />
            <input
              type='submit'
              value='Send'
            />
          </form>
          <h1>{result}</h1>
        </p>
      </div>
    </div>
  );
}
